package modchu.model;import java.util.Map;import modchu.lib.Modchu_Main;import modchu.lib.Modchu_Reflect;import modchu.lib.replacepoint.Modchu_ModelMultiReplacePoint;/** * ModelCapsの補助関数群 */public class ModchuModel_ModelCapsHelper {	public static Object getCapsValue(ModchuModel_IModelCaps pOwner, int pCapsIndex, Object ...pArg) {		return pOwner == null ? null : pOwner.getCapsValue(pCapsIndex, pArg);	}	public static Object getCapsValue(ModchuModel_IModelCaps pOwner, String pCapsName, Object ...pArg) {		return pOwner == null ? null : pOwner.getCapsValue(pOwner.getModelCaps().get(pCapsName), pArg);	}	public static int getCapsValueInt(Object o, ModchuModel_IModelCaps pOwner, int pIndex, Object ...pArg) {		o = Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ ModchuModel_IModelCaps.class, int.class, Object[].class }, o, new Object[]{ pOwner, pIndex, pArg });		if (o instanceof Boolean) {			boolean b = (Boolean) o;			return b ? 2 : 1;		}		return o != null ? (Integer) o : 0;	}	public static int getCapsValueInt(Object o, int pIndex, Object ...pArg) {		o = getCapsValueObject(o, pIndex, pArg);		if (o instanceof Boolean) {			boolean b = (Boolean) o;			return b ? 2 : 1;		}		return o != null ? (Integer) o : 0;	}	public static byte getCapsValueByte(Object o, ModchuModel_IModelCaps pOwner, int pIndex, Object ...pArg) {		o = Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ ModchuModel_IModelCaps.class, int.class, Object[].class }, o, new Object[]{ pOwner, pIndex, pArg });		if (o instanceof Boolean) {			boolean b = (Boolean) o;			return (byte) (b ? 2 : 1);		}		return o != null ? (Byte) o : 0;	}	public static byte getCapsValueByte(Object o, int pIndex, Object ...pArg) {		o = getCapsValueObject(o, pIndex, pArg);		if (o instanceof Boolean) {			boolean b = (Boolean) o;			return (byte) (b ? 2 : 1);		}		return o != null ? (Byte) o : 0;	}	public static float getCapsValueFloat(Object o, ModchuModel_IModelCaps pOwner, int pIndex, Object ...pArg) {		o = Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ ModchuModel_IModelCaps.class, int.class, Object[].class }, o, new Object[]{ pOwner, pIndex, pArg });		return o != null 				&& Modchu_Main.floatCheck(""+o) ? Float.valueOf(""+o) : 0.0F;	}	public static float getCapsValueFloat(Object o, int pIndex, Object ...pArg) {		o = getCapsValueObject(o, pIndex, pArg);		return o != null				&& Modchu_Main.floatCheck(""+o) ? Float.valueOf(""+o) : 0.0F;	}	public static double getCapsValueDouble(Object o, ModchuModel_IModelCaps pOwner, int pIndex, Object ...pArg) {		o = Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ ModchuModel_IModelCaps.class, int.class, Object[].class }, o, new Object[]{ pOwner, pIndex, pArg });		return o != null				&& Modchu_Main.doubleCheck(""+o) ? Double.valueOf(""+o) : 0.0D;	}	public static double getCapsValueDouble(Object o, int pIndex, Object ...pArg) {		o = getCapsValueObject(o, pIndex, pArg);		return o != null				&& Modchu_Main.doubleCheck(""+o) ? Double.valueOf(""+o) : 0.0D;	}	public static boolean getCapsValueBoolean(Object o, ModchuModel_IModelCaps pOwner, int pIndex, Object ...pArg) {		o = Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ ModchuModel_IModelCaps.class, int.class, Object[].class }, o, new Object[]{ pOwner, pIndex, pArg });		return o != null ? (Boolean) o : false;	}	public static boolean getCapsValueBoolean(Object o, int pIndex, Object ...pArg) {		o = getCapsValueObject(o, pIndex, pArg);		return o != null ? (Boolean) o : false;	}	public static boolean setCapsValue(Object o, ModchuModel_IModelCaps pOwner, String pCapsName, Object... pArg) {		return o == null ? false : (Boolean)Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ ModchuModel_IModelCaps.class, int.class, Object[].class }, o, new Object[]{ pOwner, ((Map) Modchu_Reflect.invokeMethod(Modchu_ModelMultiReplacePoint.class, "getModelCaps", o)).get(pCapsName), pArg });	}	public static boolean setCapsValue(ModchuModel_IModelCaps pOwner, String pCapsName, Object... pArg) {		return pOwner == null ? false : pOwner.setCapsValue(pOwner.getModelCaps().get(pCapsName), pArg);	}	private static Object getCapsValueObject(Object o, int pIndex, Object[] pArg) {		return o == null ? false : Modchu_Reflect.invokeMethod(o.getClass(), "getCapsValue", new Class[]{ int.class, Object[].class }, o, new Object[]{ pIndex, pArg });	}}