eturn false;		//int cnt = rl.replaceAll("[^,]", "").length();		return s.replaceAll("[^,]", "").length() > 7;	}	private static boolean motionStringFrameCheck(String s) {		if (s != null); else return false;		return s.indexOf(",") < 0				&& Modchu_Main.integerCheck(s) ? true : false;	}*/	private static ModchuModel_MotionData addMotionData(ModchuModel_MotionData motionData, String[] nameArray, String name, int frame, float[] dataF) {		if (name != null				&& dataF != null				&& frame > -1); else return motionData;		boolean flag = true;		if (nameArray != null) {			flag = false;			for (int i1 = 0; i1 < nameArray.length; i1++) {				if (nameArray[i1].equalsIgnoreCase(name)) {					if (debug) Modchu_Debug.mDebug("ModchuModel_MotionDataMaster addMotionData nameArray check ok.");					flag = true;					break;				}			}		}		if (!flag) {			if (debug) Modchu_Debug.mDebug("ModchuModel_MotionDataMaster addMotionData nameArray check NG return.");			return motionData;		}		motionData.addNameList(name);		motionData.putFrameStringMap(name);		//List list = motionData.getFrameStringList(name);		motionData.putDataMap(frame, new Object[]{ name, dataF });		if (debug) Modchu_Debug.mDebug("ModchuModel_MotionDataMaster addMotionData putDataList="+(name + "," + frame));		//list.add(frame);		if (frame > tempI) tempI = frame;		//motionData.putFrameStringMap(name, list);		if (debug) Modchu_Debug.mDebug("ModchuModel_MotionDataMaster addMotionData end frame="+frame);		//tempData = name;		return motionData;	}/*	private static ModchuModel_MotionData addMotionData(ModchuModel_MotionData motionData, String[] nameArray, String s) {		int i = 0;		i = s.indexOf(",");		if (i < 0) {			if (debug) Modchu_Debug.mDebug("ModchuModel_MotionDataMaster addMotionData 1 error s="+s);			return motionData;		}		String[] s0 = s.split(",");		String s1 = null;		s1 = s0[0];		if (debug) Modchu_Debug.mDebug("ModchuModel_MotionDataMaster addMotionData 1 s1="+s1);		if (s1 != null);