et(Modchu_AS.renderHelperEnableStandardItemLighting);			//Modchu_GlStateManager.disable(GL12.GL_RESCALE_NORMAL);			Modchu_AS.set(Modchu_AS.openGlHelperSetActiveTexture, Modchu_AS.getInt(Modchu_AS.openGlHelperLightmapTexUnit));			Modchu_AS.set(Modchu_AS.openGlHelperSetActiveTexture, Modchu_AS.getInt(Modchu_AS.openGlHelperDefaultTexUnit));			boolean b = Modchu_AS.getBoolean(Modchu_AS.renderManagerRenderEntityWithPosYaw, entity, 0.0D, 0.0D, 0.0D, 0.0F, 1.0F);			//Modchu_Debug.mDebug("renderManagerRenderEntityWithPosYaw b="+b);			//Modchu_Debug.mDebug("renderManagerGetEntityRenderObject="+Modchu_AS.get(Modchu_AS.renderManagerGetEntityRenderObject, entity));		} catch(Error e) {			e.printStackTrace();		} catch(Exception e) {			e.printStackTrace();		}		Modchu_GlStateManager.popMatrix();/*		//Modchu_AS.set(Modchu_AS.renderHelperDisableStandardItemLighting);		GL11.glDisable(32826);		GL11.glDisable(GL12.GL_RESCALE_NORMAL);		Modchu_AS.set(Modchu_AS.openGlHelperSetActiveTexture, Modchu_AS.getInt(Modchu_AS.openGlHelperLightmapTexUnit));		Modchu_GlStateManager.enableTexture2D();		Modchu_AS.set(Modchu_AS.openGlHelperSetActiveTexture, Modchu_AS.getInt(Modchu_AS.openGlHelperDefaultTexUnit));		Modchu_GlStateManager.disableLighting();*/	}	public static void drawMobModel2(float f, Object entity) {		drawMobModel2(f, 0.0F, 0.0F, 0.0F, 0.0F, 0.0F, 0.0F, 1.0F, 1.0F, 1.0F, entity);	}	public static void drawMobModel2(float f, float comeraPosX, float comeraPosY, float comeraPosZ, float comeraRotationX, float comeraRotationY, float comeraRotationZ, float cameraZoomX, float cameraZoomY, float cameraZoomZ, Object entity) {		//ModchuModel_Main.oldRender = true;		Modchu_GlStateManager.pushMatrix();		Modchu_GlStateManager.enableColorMaterial();		try {			//Modchu_Debug.mDebug("ModchuModel_RenderMasterBase drawMobModel2 x="+x+"y="+y);			//Modchu_GlStateManager.scale(-f, f, f);			//GL11.glLoadIdentity();			//GL11.glEnable(32826 /*GL_RESCALE_NORMAL_EXT*/);			GL11.glMatrixMode(GL11.GL_MODELVIEW);			//GL11.glMatrixMode(GL11.GL_PROJECTION);			Modchu_GlStateManager.enableColorMaterial();			Modchu_GlStateManager.enableTexture2D();			Modchu_GlStateManager.depthMask(true);			Modchu_AS.set(Modchu_AS.renderHelperEnableStandardItemLighting);			//Modchu_GlStateManager.disable(GL12.GL_RESCALE_NORMAL);			Modchu_AS.set(Modchu_AS.openGlHelperSetActiveTexture, Modchu_AS.getInt(Modchu_AS.openGlHelperLightmapTexUnit));			Modchu_AS.set(Modchu_AS.openGlHelperSetActiveTexture, Modchu_AS.getInt(Modchu_AS.openGlHelperDefaultTexUnit));			//boolean b = Modchu_AS.getBoolean(Modchu_AS.renderManagerRenderEntityWithPosYaw, entity, 0.0D, 0.0D, 0.0D, 0.0F, 1.0F);			if (Modchu_AS.getInt(Modchu_AS.entityTicksExisted, entity) == 0) {				Modchu_AS.set("Entity", "lastTickPosX", entity, Modchu_AS.getDouble(Modchu_AS.entityPosX, entity));				Modchu_AS.set("Entity", "lastTickPosY", entity, Modchu_AS.getDouble(Modchu_AS.entityPosY, entity));				Modchu_AS.set("Entity", "lastTickPosZ", entity, Modchu_AS.getDouble(Modchu_AS.entityPosZ, entity));			}			double lastTickPosX = Modchu_AS.getDouble("Entity", "lastTickPosX", entity);			double lastTickPosY = Modchu_AS.getDouble("Entity", "lastTickPosY", entity);			double lastTickPosZ = Modchu_AS.getDouble("Entity", "lastTickPosZ", entity);			double posX = Modchu_AS.getDouble(Modchu_AS.entityPosX, entity);			double posY = Modchu_AS.getDouble(Modchu_AS.entityPosY, entity);			double posZ = Modchu_AS.getDouble(Modchu_AS.entityPosZ, entity);			double d0 = lastTickPosX + (posX - lastTickPosX) * (double) f;			double d1 = lastTickPosY + (posY - lastTickPosY) * (double) f;			double d2 = lastTickPosZ + (posZ - lastTickPosZ) * (double) f;			float prevRotationYaw = Modchu_AS.getFloat(Modchu_AS.entityPrevRotationYaw, entity);			float rotationYaw = Modchu_AS.getFloat(Modchu_AS.entityRotationYaw, entity);			float f1 = prevRotationYaw + (rotationYaw - prevRotationYaw) * f;			int i = Modchu_AS.getInt(Modchu_AS.entityGetBrightnessForRender, entity, f);			if (Modchu_AS.getBoolean(Modchu_AS.entityIsBurning, entity)) i = 15728880;			int j = i % 65536;			int k = i / 65536;			Modchu_AS.set(Modchu_AS.openGlHelperSetLightmapTextureCoords, Modchu_AS.get(Modchu_AS.openGlHelperLightmapTexUnit), (float)j / 1.0F, (float)k / 1.0F);			Modchu_GlStateManager.color(1.0F, 1.0F, 1.0F, 1.0F);			double renderPosX = Modchu_AS.getDouble("RenderManager", "renderPosX");			double renderPosY = Modchu_AS.getDouble("RenderManager", "renderPosY");			double renderPosZ = Modchu_AS.getDouble("RenderManager", "renderPosZ");			//Modchu_Debug.mDebug("ModchuModel_RenderMasterBase drawMobModel2 renderPosX="+renderPosX+" renderPosY="+renderPosY+" renderPosZ="+renderPosZ);			Object render = ModchuModel_Main.renderPlayerDummyInstance;			Modchu_AS.set("Render", "doRender", new Class[]{ Modchu_Reflect.loadClass("Entity"), double.class, double.class, double.class, float.class, float.class }, render, new Object[]{ entity, d0 - renderPosX, d1 - renderPosY, d2 - renderPosZ, f1, f });			//Object renderManager = Modchu_AS.get(Modchu_AS.renderManagerInstance);			//Modchu_AS.set("RenderManager", "renderEntity", new Class[]{ Modchu_Reflect.loadClass("Entity"), float.class }, renderManager, new Object[]{ entity, 0.0F });			//Modchu_Debug.mDebug("ModchuModel_RenderMasterBase drawMobModel2 renderManagerRenderEntityWithPosYaw b="+b);			double x1 = Modchu_AS.getDouble(Modchu_AS.entityPosX, entity);			double y1 = Modchu_AS.getDouble(Modchu_AS.entityPosY, entity);			double z1 = Modchu_AS.getDouble(Modchu_AS.entityPosZ, entity);			//Modchu_Debug.mDebug("ModchuModel_RenderMasterBase drawMobModel2 x1="+x1+" y1="+y1+" z1="+z1);			//Modchu_Debug.mDebug("ModchuModel_RenderMasterBase drawMobModel2 d0="+d0+" d1="+d1+" d2="+d2);			//Modchu_Debug.mDebug("ModchuModel_RenderMasterBase drawMobModel2 renderManagerGetEntityRenderObject="+Modchu_AS.get(Modchu_AS.renderManagerGetEntityRenderObject, entity));		} catch(Error e) {			e.printStackTrace();		} catch(Exception e) {			e.printStackTrace();		}		Modchu_GlStateManager.popMatrix();	}}